import axios from "axios";

// POST Api to Post data to the server when the user clicks on Submit
export async function postData(student) {
  try {
    // Extracting educational background fields
    const { educationalBackground, ...rest } = student;
    console.log("1", educationalBackground);
    // Transforming educational background data into an array of objects
    const formattedEducationalBackground = student.map((edu) => ({
      institutionName: edu.institutionName[0], // Assuming institutionName is a single value
      passOutYear: parseInt(edu.passOutYear[0]), // Assuming passOutYear is a single value
      cgpiScore: parseFloat(edu.cgpiScore[0]), // Assuming cgpiScore is a single value
    }));
    console.log("2", formattedEducationalBackground);
    // Forming the data object to send to the server
    const data = {
      ...rest,
      educationalBackground: formattedEducationalBackground,
    };

    const response = await axios.post(
      "http://localhost:8080/api/students",
      data
    );
    return response.data;
  } catch (error) {
    console.error("Error posting data:", error);
    throw error;
  }
}

