import React, { useState } from "react";
import { useForm } from "react-hook-form";
import { yupResolver } from "@hookform/resolvers/yup";
import {
  TextField,
  Button,
  Typography,
  Box,
  Container,
  Grid,
  Paper,
} from "@mui/material";
import StudentList from "./StudentsList/StudentList.js";
import { Scrollbars } from "react-custom-scrollbars";
import schema from "./Validation/ValidationSchema.js";
import { postData } from "./PostAPI/Api.js";

const Form = ({ setSubmitted, setIsError }) => {
  const {
    register,
    handleSubmit,
    formState: { errors },
  } = useForm({
    resolver: yupResolver(schema),
  });

  const [dynamicFields, setDynamicFields] = useState([
    { institutionName: "", passOutYear: "", cgpiScore: "" },
  ]);

  const addDynamicField = () => {
    setDynamicFields([
      ...dynamicFields,
      { institutionName: "", passOutYear: "", cgpiScore: "" },
    ]);
  };

  const removeDynamicField = (index) => {
    const updatedFields = [...dynamicFields];
    updatedFields.splice(index, 1);
    setDynamicFields(updatedFields);
  };

  console.log("error", errors);

  const onSubmit = (data) => {
    // Ensure educationalBackground field is initialized as an empty array if not provided
    const studentData = {
      ...data,
      educationalBackground: data.educationalBackground || [],
    };

    console.log(studentData);
    setSubmitted(true);
    postData(studentData)
      .then((responseData) => {
        console.log("Data posted successfully:", responseData);
      })
      .catch((error) => {
        setIsError(true);
      });
  };

  return (
    <Container maxWidth="md">
      <Grid container spacing={3}>
        <Grid item xs={12} sm={6}>
          <Typography
            variant="h4"
            align="center"
            gutterBottom
            style={{ color: "whitesmoke" }}
          >
            Student Registration Form
          </Typography>
          <Paper
            elevation={0}
            sx={{
              p: 2,
              backgroundColor: "#FAF9F6",
              border: "1px solid #e0e0e0",
            }}
          >
            <Scrollbars style={{ height: 450 }}>
              <form onSubmit={handleSubmit(onSubmit)}>
                <Typography
                  variant="h6"
                  sx={{ fontFamily: "Arial, sans-serif", fontWeight: "bold" }}
                >
                  Basic Info
                </Typography>
                {/* Basic Info Fields */}
                <TextField
                  {...register("firstName")}
                  label="First Name"
                  fullWidth
                  error={!!errors.firstName}
                  helperText={errors.firstName?.message}
                  margin="normal"
                />
                <TextField
                  {...register("lastName")}
                  label="Last Name"
                  fullWidth
                  error={!!errors.lastName}
                  helperText={errors.lastName?.message}
                  margin="normal"
                />
                <TextField
                  {...register("email")}
                  label="Email"
                  fullWidth
                  error={!!errors.email}
                  helperText={errors.email?.message}
                  margin="normal"
                />
                <TextField
                  {...register("phoneNumber")}
                  label="Phone Number"
                  fullWidth
                  error={!!errors.phoneNumber}
                  helperText={errors.phoneNumber?.message}
                  margin="normal"
                />
                {dynamicFields.map((field, index) => (
                  <div key={index}>
                    <TextField
                      {...register(
                        `educationalBackground[${index}].institutionName`
                      )}
                      label="Institution Name"
                      fullWidth
                      error={
                        !!errors.educationalBackground?.[index]?.institutionName
                      }
                      // helperText={
                      //   errors.educationalBackground?.[index]?.institutionName
                      //     ?.message
                      // }
                      margin="normal"
                    />
                    <TextField
                      {...register(
                        `educationalBackground[${index}].passOutYear`
                      )}
                      label="Pass-out Year"
                      fullWidth
                      type="number"
                      error={
                        !!errors.educationalBackground?.[index]?.passOutYear
                      }
                      helperText={
                        errors.educationalBackground?.[index]?.passOutYear
                          ?.message
                      }
                      margin="normal"
                    />
                    <TextField
                      {...register(`educationalBackground[${index}].cgpiScore`)}
                      label="CGPI/Score"
                      fullWidth
                      error={!!errors.educationalBackground?.[index]?.cgpiScore}
                      helperText={
                        errors.educationalBackground?.[index]?.cgpiScore
                          ?.message
                      }
                      margin="normal"
                    />
                    <Button onClick={() => removeDynamicField(index)}>
                      Remove
                    </Button>
                  </div>
                ))}

                <Button onClick={addDynamicField}>Add Field</Button>
                <Box mt={2}>
                  <Button variant="contained" type="submit">
                    Submit
                  </Button>
                </Box>
              </form>
            </Scrollbars>
          </Paper>
        </Grid>
        <Grid item xs={12} sm={6}>
          <StudentList />
        </Grid>
      </Grid>
    </Container>
  );
};

export default Form;

